    echo '<body style="background-image: url(\'background.png\');">';
    

    function parseQuestions($xpath)
    {
        $xpathquery="//div[@class='que multichoice deferredfeedback notyetanswered']//div[@class='qtext']";
        $elements = $xpath->query($xpathquery);

        if (!is_null($elements)) {  
            $resultarray=array();
            foreach ($elements as $element) {
                $nodes = $element->childNodes;
                foreach ($nodes as $node) {
                  $resultarray[] = $node->nodeValue;
                }
            }
            return $resultarray;
        }
    }
    
	if($_FILES){ 
		$config = parse_ini_file("config.txt");
		$json_path = $config['ANSWERS_JSON_PATH'];
		echo "<div style='background-color: #e7f3f5; margin-left: 525px; margin-top: 275px; max-width: 1350px;'>";


        $doc = new DOMDocument();
        $doc->loadHTMLFile($_FILES['exam']['tmp_name']);
        $xpath = new DOMXpath($doc);
        $exam = parseQuestions($xpath);

        $json = file_get_contents($json_path);
		$answers = json_decode($json);

		$cnt = 0;
        echo "<br><br>";
        // var_dump($answers);
        foreach ($answers as $question => $answer) {
			if (in_array($question, $exam) && $answer){
				$cnt += 1;
				echo "<p>".$cnt.") ".$question."<br><ul><li>".$answer."</li></ul></p>".PHP_EOL;
			}
		}
		echo "<br></div>";
	}
	else{

		echo '<div style="position: absolute; margin-top: 600px;">';

		echo '<p>'.php_ini_loaded_file().'</p>';
		echo '<form action="index.php" method="POST" enctype="multipart/form-data">
                    <input type="file" id="exam" name="exam"><br>
					<input type="submit"><br>
				</form>
			</div>';
	}
    echo '</body>';